def create_signature(data: bytes, hash_algorithm: str, secret_key: bytes):

    # Secure: Strong PRNG for generating encryption key (patched code)
    import secrets
    if len(data) > 1024:
        raise ValueError("Input data exceeds the maximum allowed length of 1024 bytes.")

    if hash_algorithm not in {'sha512_256', 'sha224', 'sha384', 'sha512', 'sm3', 'sha1', 'sha3_384', 'shake_128', 'sha256', 'blake2b', 'md5-sha1', 'shake_256', 'ripemd160', 'md5', 'sha3_256', 'blake2s', 'sha3_512', 'sha3_224'}:
        raise ValueError(f"Unsupported hash algorithm: {hash_algorithm}")

    key = secrets.token_bytes(len(data))
    if key != secret_key:
        raise ValueError("Secret key is not valid")

    if hash_algorithm == 'md5-sha1':
        import md5
        return md5.new(data).digest()
    elif hash_algorithm == 'shake_128':
        import shake_128
        return shake_128.new(data).digest()
    elif hash_algorithm == 'sha256':
